dt-option.example:
new DataTable('#myTable', {
	columnControl: ['order'],
	ordering: {
        indicators: false,
        handler: false
    }
});

new DataTable('#example', {
    columnControl: [
        {
            target: 0,
            content: ['order']
        },
        {
            target: 1,
            content: ['search']
        }
    ],
    ordering: {
        indicators: false,
        handler: false
    }
});

new DataTable('#example', {
    columnControl: [
        {
            target: 0,
            content: ['order']
        },
        {
            target: 'tfoot',
            content: ['search']
        }
    ],
    ordering: {
        indicators: false,
        handler: false
    }
});
