dt-example.js:
$('#example').DataTable({
	columnControl: ['order', ['reorderLeft', 'reorderRight']],
	ordering: {
		indicators: false,
		handler: false
	}
});

dt-example.js-vanilla:
new DataTable('#example', {
	columnControl: ['order', ['reorderLeft', 'reorderRight']],
	ordering: {
		indicators: false,
		handler: false
	}
});
