dt-example.js:
$('#example').DataTable({
	colReorder: {
        columns: ':not(:first-child, :last-child)'
    },
	columnControl: {
		content: ['order', 'reorder']
	},
	columnDefs: [{
		targets: [0, -1],
		columnControl: {
			content: ['order']
		}
	}],
	ordering: {
		indicators: false,
		handler: false
	}
});

dt-example.js-vanilla:
new DataTable('#example', {
	colReorder: {
        columns: ':not(:first-child, :last-child)'
    },
	columnControl: {
		content: ['order', 'reorder']
	},
	columnDefs: [{
		targets: [0, -1],
		columnControl: {
			content: ['order']
		}
	}],
	ordering: {
		indicators: false,
		handler: false
	}
});
