dt-example.js:
$('#example').DataTable({
	columnControl: {
		content: ['order', 'reorder', 'colVisDropdown']
	},
	ordering: {
		indicators: false,
		handler: false
	}
});

dt-example.js-vanilla:
new DataTable('#example', {
	columnControl: {
		content: ['order', 'reorder', 'colVisDropdown']
	},
	ordering: {
		indicators: false,
		handler: false
	}
});
