dt-example.js:
$('#example').DataTable({
	columnControl: ['order', 'reorder', 'colVisDropdown'],
	ordering: {
		indicators: false,
		handler: false
	}
});

dt-example.js-vanilla:
new DataTable('#example', {
	columnControl: ['order', 'reorder', 'colVisDropdown'],
	ordering: {
		indicators: false,
		handler: false
	}
});
