dt-example.js:
$('#example').DataTable({
	columnControl: {
		content: [
			[
				'orderAsc',
				'orderDesc',
				{
					extend: 'dropdown',
					icon: 'columns',
					text: 'Column visibility',
					content: ['colVis']
				}
			]
		]
	},
	ordering: {
		indicators: false,
		handler: false
	}
});

dt-example.js-vanilla:
new DataTable('#example', {
	columnControl: {
		content: [
			[
				'orderAsc',
				'orderDesc',
				{
					extend: 'dropdown',
					icon: 'columns',
					text: 'Column visibility',
					content: ['colVis']
				}
			]
		]
	},
	ordering: {
		indicators: false,
		handler: false
	}
});
