dt-example.js:
$('#example').DataTable({
	columnControl: {
		content: [['orderAsc', 'orderDesc', 'spacer', 'colVis']]
	},
	language: {
		columnControl: {
			colVis: 'Change columns'
		}
	}
});

dt-example.js-vanilla:
new DataTable('#example', {
	columnControl: {
		content: [['orderAsc', 'orderDesc', 'spacer', 'colVis']]
	},
	language: {
		columnControl: {
			colVis: 'Change columns'
		}
	}
});
