dt-example.js:
$('#example').DataTable({
	columnControl: [
		{
			target: 0,
			content: ['order']
		},
		{
			target: 1,
			content: ['search']
		}
	],
	ordering: {
		indicators: false,
		handler: false
	}
});

dt-example.js-vanilla:
new DataTable('#example', {
	columnControl: [
		{
			target: 0,
			content: ['order']
		},
		{
			target: 1,
			content: ['search']
		}
	],
	ordering: {
		indicators: false,
		handler: false
	}
});
