dt-example.js:
$('#example').DataTable({
	columnControl: [
		'order',
		[
			'orderAsc',
			'orderDesc',
			{
				extend: 'dropdown',
				text: 'Column visibility',
				content: ['colVis']
			}
		]
	],
	ordering: {
		indicators: false,
		handler: false
	}
});

dt-example.js-vanilla:
new DataTable('#example', {
	columnControl: [
		'order',
		[
			'orderAsc',
			'orderDesc',
			{
				extend: 'dropdown',
				text: 'Column visibility',
				content: ['colVis']
			}
		]
	],
	ordering: {
		indicators: false,
		handler: false
	}
});
