dt-example.js:
$('#example').DataTable({
	ordering: {
		indicators: false,
		handler: false
	},
	columnControl: {
		content: [
			[
				'orderAsc',
				'orderDesc',
				['title', 'orderAsc']
			]
		]
	}
});

dt-example.js-vanilla:
new DataTable('#example', {
	ordering: {
		indicators: false,
		handler: false
	},
	columnControl: {
		content: [
			[
				'orderAsc',
				'orderDesc',
				['title', 'orderAsc']
			]
		]
	}
});
