dt-example.js:
$('#example').DataTable({
	columnControl: ['order', 'spacer', ['orderAsc', 'orderDesc', 'spacer', 'search']],
	ordering: {
		indicators: false,
		handler: false
	}
});

dt-example.js-vanilla:
new DataTable('#example', {
	columnControl: ['order', 'spacer', ['orderAsc', 'orderDesc', 'spacer', 'search']],
	ordering: {
		indicators: false,
		handler: false
	}
});
