dt-example.js:
$('#example').DataTable({
	ordering: {
		indicators: false,
		handler: false
	},
	columnControl: {
		content: [
			'orderAsc',
			'orderDesc',
			'spacer',
			['orderAsc', 'orderDesc', 'spacer', 'search']
		]
	}
});

dt-example.js-vanilla:
new DataTable('#example', {
	ordering: {
		indicators: false,
		handler: false
	},
	columnControl: {
		content: [
			'orderAsc',
			'orderDesc',
			'spacer',
			['orderAsc', 'orderDesc', 'spacer', 'search']
		]
	}
});
