dt-example.js:
$('#example').DataTable({
	ordering: {
		indicators: false,
		handler: false
	},
	columnControl: {
		target: 1,
		content: ['searchText']
	},
	stateSave: true
});

dt-example.js-vanilla:
new DataTable('#example', {
	ordering: {
		indicators: false,
		handler: false
	},
	columnControl: {
		target: 1,
		content: [{
			extend: 'searchText',
			text: 'Search:',
			placeholder: 'Search'
		}]
	},
	stateSave: true
});
