dt-example.js:
$('#example thead tr').clone().appendTo('#example thead');
$('#example thead tr:eq(1) th').empty();

$('#example').DataTable({
	ordering: {
		indicators: false,
		handler: false
	},
	columnControl: {
		target: 1,
		content: ['orderAsc', 'orderDesc']
	}
});

dt-example.js-vanilla:
$('#example thead tr').clone().appendTo('#example thead');
$('#example thead tr:eq(1) th').empty();

new DataTable('#example', {
	ordering: {
		indicators: false,
		handler: false
	},
	columnControl: {
		target: 1,
		content: ['orderAsc', 'orderDesc']
	}
});
