dt-example.js:
$('#example').DataTable({
	ordering: {
		indicators: false,
		handler: false
	},
	columnControl: {
		content: ['order', ['orderAsc', 'orderDesc', 'orderAddAsc', 'orderAddDesc']]
	}
});

dt-example.js-vanilla:
new DataTable('#example', {
	ordering: {
		indicators: false,
		handler: false
	},
	columnControl: {
		content: ['order', ['orderAsc', 'orderDesc', 'orderAddAsc', 'orderAddDesc']]
	}
});
