dt-example.js:
$('#example').DataTable({
	ordering: {
		indicators: false
	},
	columnControl: {
		content: [{ extend: 'order', statusOnly: true }]
	}
});

dt-example.js-vanilla:
new DataTable('#example', {
	ordering: {
		indicators: false
	},
	columnControl: {
		content: [{ extend: 'order', statusOnly: true }]
	}
});
