dt-example.js:
$('#example').DataTable({
	columnControl: [
		{
			extend: 'rowGroup',
			order: false
		},
		'order'
	],
	ordering: {
		indicators: false,
		handler: false
	}
});

dt-example.js-vanilla:
new DataTable('#example', {
	columnControl: [
		{
			extend: 'rowGroup',
			order: false
		},
		'order'
	],
	ordering: {
		indicators: false,
		handler: false
	}
});
