dt-example.js:
new DataTable('#example', {
	ajax: '../../../../examples/ajax/data/objects.txt',
	columnControl: [
		{
			target: 0,
			content: ['order']
		},
		{
			target: 1,
			content: ['search']
		}
	],
    columns: [
        { data: 'name' },
        { data: 'position' },
        { data: 'office' },
        { data: 'extn' },
        { data: 'start_date' },
        { data: 'salary' }
    ],
	ordering: {
		indicators: false,
		handler: false
	},
	stateSave: true
});

