dt-example.js:
$('#example').DataTable({
	ajax: '../../../../examples/ajax/data/objects.txt',
	columnControl: ['order', 'searchCollection'],
	columns: [
		{ data: 'name' },
		{ data: 'position' },
		{ data: 'office' },
		{ data: 'extn' },
		{ data: 'start_date' },
		{ data: 'salary' }
	],
	ordering: {
		indicators: false,
		handler: false
	}
});

dt-example.js-vanilla:
new DataTable('#example', {
	ajax: '../../../../examples/ajax/data/objects.txt',
	columnControl: ['order', 'searchCollection'],
	columns: [
		{ data: 'name' },
		{ data: 'position' },
		{ data: 'office' },
		{ data: 'extn' },
		{ data: 'start_date' },
		{ data: 'salary' }
	],
	ordering: {
		indicators: false,
		handler: false
	}
});
