dt-example.js:
new DataTable('#example', {
	ajax: '../data/datetime.json',
	columnControl: [
		{
			target: 0,
			content: ['orderStatus']
		},
		{
			target: 1,
			content: [
				{
					extend: 'search',
					mask: 'YYYY-MM-DD',
					format: 'D MMM YYYY'
				}
			]
		}
	],
	columns: [
		{data: 'name'},
		{data: 'position'},
		{data: 'office'},
		{data: 'extn'},
		{data: 'start_date', render: DataTable.render.datetime()},
		{data: 'salary'}
	],
	ordering: {
		indicators: false
	}
});

