dt-example.js:
new DataTable('#example', {
	ordering: {
		indicators: false,
		handler: false
	},
	columnControl: ['order', ['searchList']],
	columnDefs: [{
		target: 4,
		render: DataTable.render.datetime('Do MMM YYYY')
	}]
});

