dt-example.js:
new DataTable('#example', {
	ordering: {
		indicators: false,
		handler: false
	},
	columnControl: [
		{
			target: 0,
			content: ['order']
		},
		{
			target: 1,
			content: ['searchText']
		}
	],
	columnDefs: [
		{
			targets: [3, 5],
			columnControl: {
				target: 1,
				content: ['searchNumber']
			}
		},
		{
			targets: [4],
			columnControl: {
				target: 1,
				content: ['searchDateTime']
			},
			render: DataTable.render.datetime('d MMM yyyy', 'MMM d, yy', 'en')
		}
	]
});

