dt-example.js:
new DataTable('#example', {
	ordering: {
		indicators: false,
		handler: false
	},
	columnControl: ['order', ['search']],
	columnDefs: [{
		targets: [1, 2],
		columnControl: ['order', ['searchList']]
	}]
});

